from flask import Flask, render_template, url_for, flash, redirect
from forms import SurveyForm

app = Flask(__name__)
app.config['SECRET_KEY'] = '5791628bb0b13ce0c676dfde280ba245'


@app.route('/')
def Financehome():
    return render_template("Finance.html", title='Finance')


@app.route('/Survey', methods=['GET', 'POST'])
def Survey():
    form = SurveyForm()
    if form.validate_on_submit():
        flash(f'F&B: {form.f_budget.data}\n Essential: {form.e_budget.data}\n Leisure: {form.l_budget.data}\n Others: {form.o_budget.data} Submitted!', 'success')
        return redirect(url_for('Advisor'))
    return render_template("Survey.html", title='Survey', form=form)


@app.route('/Savings')
def Savings():
    return render_template("Savings.html", title='Savings')


@app.route('/Spending')
def Spending():
    return render_template("Spending.html", title='Spending')


@app.route('/Advisor')
def Advisor():
    return render_template("Advisor.html", title='Advisor')


